              <option value="student">H·ªçc sinh</option>
              <option value="teacher">Gi√°o vi√™n</option>
            </select>
          </div>

          <div className="form-group">
            <label>M·∫≠t kh·∫©u</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Nh·∫≠p m·∫≠t kh·∫©u"
