import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../context/AuthContext';
import { chatAPI } from '../services/api';
import { format } from 'date-fns';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import './Chat.css';

function Chat() {
  const { user, logout } = useAuth();
  const [sessions, setSessions] = useState([]);
  const [currentSession, setCurrentSession] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    loadSessions();
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const loadSessions = async () => {
    try {
      const response = await chatAPI.getSessions();
      setSessions(response.data);
    } catch (error) {
      console.error('Error loading sessions:', error);
    }
  };

  const loadSession = async (sessionId) => {
    try {
      const response = await chatAPI.getSession(sessionId);
      setCurrentSession(response.data);
      setMessages(response.data.messages || []);
    } catch (error) {
      console.error('Error loading session:', error);
    }
  };

  const createNewChat = async () => {
    try {
      const response = await chatAPI.createSession({
        title: 'Cu·ªôc tr√≤ chuy·ªán m·ªõi',
      });
      setSessions([response.data, ...sessions]);
      setCurrentSession(response.data);
      setMessages([]);
    } catch (error) {
      console.error('Error creating session:', error);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || loading) return;

    let sessionToUse = currentSession;

    // Create new session if none exists
    if (!sessionToUse) {
      try {
        const response = await chatAPI.createSession({
          title: 'Cu·ªôc tr√≤ chuy·ªán m·ªõi',
        });
        sessionToUse = response.data;
        setCurrentSession(response.data);
        setSessions([response.data, ...sessions]);
      } catch (error) {
        console.error('Error creating session:', error);
        return;
      }
    }

    const userMessage = {
      role: 'user',
      content: inputMessage,
      created_at: new Date().toISOString(),
    };

    setMessages([...messages, userMessage]);
    setInputMessage('');
    setLoading(true);

    try {
      const response = await chatAPI.sendMessage(sessionToUse.id, {
        content: inputMessage,
      });
      setMessages((prev) => [...prev, response.data]);

      // Reload sessions to update title
      loadSessions();
    } catch (error) {
      console.error('Error sending message:', error);
    } finally {
      setLoading(false);
    }
  };

  const deleteSession = async (sessionId) => {
    if (!window.confirm('B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a cu·ªôc tr√≤ chuy·ªán n√†y?')) return;

    try {
      await chatAPI.deleteSession(sessionId);
      setSessions(sessions.filter((s) => s.id !== sessionId));
      if (currentSession?.id === sessionId) {
        setCurrentSession(null);
        setMessages([]);
      }
    } catch (error) {
      console.error('Error deleting session:', error);
    }
  };

  return (
    <div className="chat-container">
      {/* Sidebar */}
      <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
        <div className="sidebar-header">
          <button className="new-chat-btn" onClick={createNewChat}>
            + Cu·ªôc tr√≤ chuy·ªán m·ªõi
          </button>
        </div>

        <div className="sessions-list">
          {sessions.map((session) => (
            <div
              key={session.id}
              className={`session-item ${
                currentSession?.id === session.id ? 'active' : ''
              }`}
              onClick={() => loadSession(session.id)}
            >
              <div className="session-title">{session.title}</div>
              <div className="session-actions">
                <button
                  className="delete-btn"
                  onClick={(e) => {
                    e.stopPropagation();
                    deleteSession(session.id);
                  }}
                >
                  üóëÔ∏è
                </button>
              </div>
            </div>
          ))}
        </div>

        <div className="sidebar-footer">
          <div className="user-info">
            <div className="user-name">{user?.full_name || user?.username}</div>
            <div className="user-role">
              {user?.role === 'teacher' ? 'üë®‚Äçüè´ Gi√°o vi√™n' : 'üë®‚Äçüéì H·ªçc sinh'}
            </div>
          </div>
          <button className="logout-btn" onClick={logout}>
            ƒêƒÉng xu·∫•t
          </button>
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="chat-main">
        <div className="chat-header">
          <button
            className="sidebar-toggle"
            onClick={() => setSidebarOpen(!sidebarOpen)}
          >
            ‚ò∞
          </button>
          <h2>{currentSession?.title || 'Chatbot T√¢m L√Ω H·ªçc Sinh'}</h2>
        </div>

        <div className="messages-container">
          {messages.length === 0 && !currentSession && (
            <div className="welcome-message">
              <h1>üéì Chatbot T√¢m L√Ω</h1>
              <p>
                Xin ch√†o! T√¥i l√† chatbot h·ªó tr·ª£ t√¢m l√Ω cho h·ªçc sinh. B·∫°n c√≥ th·ªÉ
                chia s·∫ª m·ªçi v·∫•n ƒë·ªÅ v·ªÅ h·ªçc t·∫≠p, cu·ªôc s·ªëng ho·∫∑c nh·ªØng kh√≥ khƒÉn
                c·ªßa m√¨nh v·ªõi t√¥i.
              </p>
              <div className="example-questions">
                <div className="example-title">B·∫°n c√≥ th·ªÉ h·ªèi:</div>
                <div
                  className="example-item"
                  onClick={() => setInputMessage('T√¥i c·∫£m th·∫•y cƒÉng th·∫≥ng v·ªõi b√†i ki·ªÉm tra s·∫Øp t·ªõi')}
                >
                  üí≠ T√¥i c·∫£m th·∫•y cƒÉng th·∫≥ng v·ªõi b√†i ki·ªÉm tra s·∫Øp t·ªõi
                </div>
                <div
                  className="example-item"
                  onClick={() => setInputMessage('L√†m th·∫ø n√†o ƒë·ªÉ c·∫£i thi·ªán k·ªπ nƒÉng giao ti·∫øp?')}
                >
                  üó£Ô∏è L√†m th·∫ø n√†o ƒë·ªÉ c·∫£i thi·ªán k·ªπ nƒÉng giao ti·∫øp?
                </div>
                <div
                  className="example-item"
                  onClick={() => setInputMessage('T√¥i c√≥ th·ªÉ t√¨m s·ª± h·ªó tr·ª£ ·ªü ƒë√¢u trong tr∆∞·ªùng?')}
                >
                  üè´ T√¥i c√≥ th·ªÉ t√¨m s·ª± h·ªó tr·ª£ ·ªü ƒë√¢u trong tr∆∞·ªùng?
                </div>
              </div>
            </div>
          )}

          {messages.map((message, index) => (
            <div key={index} className={`message ${message.role}`}>
              <div className="message-avatar">
                {message.role === 'user' ? 'üë§' : 'ü§ñ'}
