          <h1>üéì Chatbot T√¢m L√Ω</h1>
          <p>H·ªó tr·ª£ t√¢m l√Ω h·ªçc sinh</p>
        </div>

        <form onSubmit={handleSubmit} className="auth-form">
          <h2>ƒêƒÉng Nh·∫≠p</h2>

          {error && <div className="error-message">{error}</div>}

          <div className="form-group">
            <label>T√™n ƒëƒÉng nh·∫≠p</label>
            <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Nh·∫≠p t√™n ƒëƒÉng nh·∫≠p" required />
          </div>

          <div className="form-group">
            <label>M·∫≠t kh·∫©u</label>
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Nh·∫≠p m·∫≠t kh·∫©u" required />
          </div>

          <button type="submit" disabled={loading} className="auth-button">
            {loading ? 'ƒêang ƒëƒÉng nh·∫≠p...' : 'ƒêƒÉng Nh·∫≠p'}
